#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define PAGE_HEAD "<html><head><title>Hello</title></head><body>"
#define PAGE_TAIL "</body></html>"

char* escape_html(char* input) {
  char* result = malloc(strlen(input) * 2 + 1);
  for (int i = 0; i < strlen(input); i++) {
    if (input[i] == '&') {
      strcpy(result, "&amp;");
      result += 5;
    } else if (input[i] == '<') {
      strcpy(result, "&lt;");
      result += 4;
    } else if (input[i] == '>') {
      strcpy(result, "&gt;");
      result += 4;
    } else {
      *result++ = input[i];
    }
  }
  *result = '\0';
  return result;
}

