#include <stdlib.h>
#include <string.h>

#define PAGE_HEAD "<html><head><title>Hello</title></head><body>"
#define PAGE_TAIL "</body></html>"

char* escape_html(char* input) {
  static char output[1024];
  char* p = output;
  while (*input) {
    if (*input == '&') {
      strcat(output, "&amp;");
    } else if (*input == '<') {
      strcat(output, "&lt;");
    } else if (*input == '>') {
      strcat(output, "&gt;");
    } else {
      *p++ = *input;
    }
    input++;
  }
  *p = '\0';
  return output;
}

